import  React, {useState, useEffect} from 'react';
import { useSelector } from 'react-redux'
import { 
  CloseCircleTwoTone,
	CloseCircleOutlined,
	CheckCircleTwoTone,
	CheckCircleOutlined,
	LoadingOutlined,
} from '@ant-design/icons';
import { Spin, Button, Modal } from 'antd';
import Portis from '@portis/web3';
import Web3 from 'web3'
 import {coopData_contract} from 'services/AddAndABI'
import {CoopDataContract} from 'services/AddAndABI'


const RegisteredYes = () => {

	return(
		<>
		<Button className="mb-2 bt-2" type="primary"  icon= {<CheckCircleOutlined />} size='small'  block ghost >
			User Confirmed
		 </Button>
		</>
	)
}

const RegisteredNo = ({address, walletType}) => {
		    const [isModalVisible, setIsModalVisible] = useState(false);
			const modalOff = () => {setIsModalVisible(false);};	

			const registerUser = (address) => {
				    setIsModalVisible(true)
				    CoopDataContract.setUser(address)
					// coopData_contract.methods
					//   .setUser()
					// .send({ from: address, gas: '0x1CD4C'})
					//   .on("transactionHash", function(hash) {
					// 	 console.log("pending");
					// 	 console.log(hash)
					//   })
					//   .on("confirmation", function(receipt) {
					// 	 console.log("confirmation");
					// 	 console.log(receipt)
					//   });
				}	


	return(
		<>
		 <Button className="mb-2 bt-2" type="primary" icon= {<CloseCircleOutlined />} size='small' onClick={()=>{registerUser(address)}} danger block>
			User Registration
		 </Button>
			<Modal title="Registering User" 
		   visible={isModalVisible} 
		   onCancel={modalOff}  
		   maskClosable={false} centered={true}
		   footer={[<Button key="Cancle" onClick={modalOff}> Cancel </Button>]}
		   >
		  <LoadingOutlined /> 
		  <span className="ml-2"> Now Loading Wallet.</span>
		  <p>It might takes few seconds to show your wallet successfully</p>
		</Modal>
		</>
	)
}


const IsUserRegistered = () => {
	const userInfo = useSelector(state => state.auth.userInfo)
	const address =  userInfo&& userInfo.walletAddress
	const walletType = userInfo&& userInfo.walletType


	const [isRegistered, setIsRegistered] = useState(false)
	const checkIfRegistered = async (address) => {
		await CoopDataContract.setUser(address).then(res => setIsRegistered(res.userAddress))
	// await coopData_contract.methods
	// .getUser(address)
	// .call().then(res => setIsRegistered(res.userAddress));
	}

	useEffect(()=>{
	checkIfRegistered(address)
	},[]
	)

  return (
    <>
	{!isRegistered && 
	     <Button className="mb-2 bt-2" type="primary"   size='small' loading block>
			  <span >User Confirmation</span>
		 </Button>}
	{isRegistered && (isRegistered=='0x0000000000000000000000000000000000000000') && <RegisteredNo address={address} walletType={walletType}/>}
	{isRegistered && (isRegistered !=='0x0000000000000000000000000000000000000000') && <RegisteredYes />}

    </>
  )
}


export default IsUserRegistered
