import React, {useState, useEffect} from 'react'
import { Row, Col, Button, Card, Avatar, Dropdown, Table, Menu, Tag } from 'antd';
import PropTypes from "prop-types";
import { ArrowUpOutlined, ArrowDownOutlined, CopyOutlined, FolderOpenOutlined, StopTwoTone, EllipsisOutlined,LockOutlined, UnlockOutlined  } from '@ant-design/icons';
import { useSelector, useDispatch  } from 'react-redux'
import {getTacBalance, getEtherBalance} from 'services/EtherScanService'
import CryptoStatus from './CryptoStatus'
import  {getTacWethPair}  from 'redux/actions/Uniswap'



const AssetWidget = () => {
    const {userInfo} = useSelector(state => state.auth)

	const {USDCTACpair, USDCWETHPair, TACWETHPair} = useSelector(state => state.uniswap)
	const test = useSelector(state => state.uniswap)
	console.log(test)

	const [tacBalance, setTacBalance] = useState(0)
	const tacBalanceFixed = tacBalance.toFixed(3)
	const tacBalanceInteger = Math.floor(tacBalance)
	const tacBalanceDecimal =  Math.floor( (tacBalance-Math.floor(tacBalance))*1000  ) 
	const tacBalanceToDollar = (USDCTACpair && (tacBalance * USDCTACpair).toFixed(2))



	useEffect(()=>{
		getTacBalance(userInfo.walletAddress).then(res=>setTacBalance(res))
	},[userInfo])

	const [ethBalance, setEthBalance] = useState(0)
	const ethBalanceFixed = ethBalance.toFixed(3)
	const ethBalanceDollor = (ethBalance* USDCWETHPair).toFixed(2)

	useEffect(()=>{
		getEtherBalance(userInfo.walletAddress).then(res=>setEthBalance(res))
	},[userInfo])


	return (
		<Card title='My Assets'>

			<div className={`d-flex align-items-center justify-content-between mb-4`}>
				<CryptoStatus name={'Taekwondo Access Credit(Unlocked)'} text={<UnlockOutlined className='text-dark' />}  subTitle={tacBalance + 'TAC * $' + USDCTACpair}/>
				<div>
					<div className="avatar-status-name text-success" >　</div>		
					<div className=" avatar-status-name">$667.45</div>

				</div>
			</div>
			<div className={`d-flex align-items-center justify-content-between mb-4`}>
				<CryptoStatus name={'Taekwondo Access Credit(Locked)'}  text={<LockOutlined className='text-dark' />} subTitle={'342.413 TAC*$1.85'}/>
				<div>
					<div className="avatar-status-name text-success" > 　</div>							
					<div className=" avatar-status-name">$667.45</div>	
				</div>
			</div>
			<div className={`d-flex align-items-center justify-content-between mb-4`}>
				<CryptoStatus name={'Ethereum'} src={'https://s3.amazonaws.com/token-icons/eth.png'} subTitle={ethBalanceFixed + ' ETH'}/>
				<div>
					<div className="avatar-status-name text-success" > 　 </div>			
					<div className=" avatar-status-name">$ {ethBalanceDollor} </div>					
				</div>
			</div>			
		</Card>
	)
}


export default AssetWidget
