import React from 'react'
import { Card } from 'antd';
import PropTypes from "prop-types";
import { ArrowUpOutlined, ArrowDownOutlined, CopyOutlined } from '@ant-design/icons';
import IsUserRegistered from './IsUserRegistered'
import IsTACApproved from './IsTACApproved'

const WalletWidget = ({ title, tacBalance, status, address, prefix, extra }) => {
	return (
		<Card title={title} description={address}  extra={extra}>
			<div className={`${prefix? 'd-flex': ''} ${title ? 'mt-0':''}`}>
				{prefix ? <div className="mr-2">{prefix}</div> : null}
				<div>
					<div className="d-flex align-items-center">
						<h1 className="mb-0 font-weight-bold">{tacBalance}</h1>

							<span className='font-size-md font-weight-bold ml-3 text-success' >
								TAC
							</span> 
					</div>
					{address && <div className="text-gray-light mt-1">{address} <CopyOutlined /></div>}
				</div>
			</div>
			<div className="mt-5">
		<IsUserRegistered />
		<IsTACApproved />
				</div>
		</Card>
	)
}

WalletWidget.propTypes = {
  	title: PropTypes.oneOfType([
		PropTypes.string,
		PropTypes.element
	]),
	tacBalance: PropTypes.number,
	address: PropTypes.string,
	status: PropTypes.string,
	prefix: PropTypes.element,
	extra: PropTypes.object
};

export default WalletWidget
