import React, {useEffect, useState} from 'react'
import { Row, Col, Button, Card, Avatar, Dropdown, Table, Menu, Tag } from 'antd';
import PropTypes from "prop-types";
import { ArrowUpOutlined, ArrowDownOutlined, CopyOutlined, FolderOpenOutlined, StopTwoTone, EllipsisOutlined } from '@ant-design/icons';
import { useSelector, useDispatch  } from 'react-redux'
import  {getTacWethPair}  from 'redux/actions/Uniswap'
import {getTacBalance, getEtherBalance} from 'services/EtherScanService'

const WalletWidgetOption = () => {

	return(
  <Menu>
    <Menu.Item key="0">
      <span>
        <div className="d-flex align-items-center">
          <CopyOutlined />
          <span className="ml-3 ">Copy Address</span>
        </div>
      </span>
    </Menu.Item>
	<Menu.Item key="1" onClick={ () => {}}>
      <span>
        <div className="d-flex align-items-center">
          <FolderOpenOutlined />
          <span className="ml-3 ">Open Wallet</span>
        </div>
      </span>
    </Menu.Item>	
    <Menu.Item key="2" onClick={ () => {}  }>
      <span>
        <div className="d-flex align-items-center">
          <StopTwoTone twoToneColor="#ff6b72"/>
          <span className="ml-3 text-danger font-weight-bold">Disconnect</span>
        </div>
      </span>
    </Menu.Item>
  </Menu>
)
} 

const CardDropdown = (menu) => (
  <Dropdown overlay={menu} trigger={['click']} placement="bottomRight">
    <a href="/#" className="text-gray font-size-lg" onClick={e => e.preventDefault()}>
      <EllipsisOutlined />
    </a>
  </Dropdown>
)

const WalletWidget = () => {


	// const {userInfo} = useSelector(state => state.auth)
	// const {USDCTACpair} = useSelector(state => state.uniswap)

	// const [tacBalance, setTacBalance] = useState(0)
	const tacBalance = '123.22'
	const tacBalanceInteger = Math.floor(tacBalance)
	const tacBalanceDecimal =  Math.floor( (tacBalance-Math.floor(tacBalance))*1000  ) 
	const tacBalanceToDollar = (USDCTACpair && (tacBalance * USDCTACpair).toFixed(2))


// 	useEffect(()=>{

// 		getTacBalance(userInfo.walletAddress).then(res=>setTacBalance(res))
// 		//getEtherBalance(userInfo.walletAddress).then(res=>setTacBalance(res))
// 	},[userInfo])

	return (
		<Card title='My Wallet' description='헬로?'  extra={CardDropdown(WalletWidgetOption)}>
			<div className= 'd-flex mt-0'>
				<div>
					<div className="d-flex align-items-center">
						<span className = 'mb-0' style={{fontSize: '40px', fontWeight: '600', letterSpacing: '-1px' , color: '#15151f'}}>{String.fromCharCode(parseInt('20AE',16))}</span>
						<span className = 'mb-0' style={{fontSize: '36px', fontWeight: '600', letterSpacing: '-1px' , color: '#15151f'}}>{tacBalanceInteger}</span>
						<span className = 'mb-0' style={{fontSize: '36px', fontWeight: '600', letterSpacing: '-1px' , color: '#d0d0d2'}}>.</span>
						<span className = 'mb-0' style={{fontSize: '36px', fontWeight: '600', letterSpacing: '-1px' , color: '#d0d0d2'}}>{tacBalanceDecimal}</span>				
			            <span className = 'ml-3' style={{fontSize: '20px', fontWeight: '500', letterSpacing: '-0.4px', color: '#1cc760'}} >${tacBalanceToDollar}</span> 	
					</div>
				    <div className="text-gray-light mt-0"> 
						{userInfo && <a href={`https://rinkeby.etherscan.io/address/${userInfo.walletAddress}`} target="_blank">{userInfo.walletAddress}</a>   }
				    </div>
				</div>
			</div>
			<div className="mt-5"> </div>
		</Card>
	)
}


export default WalletWidget
