import React , {useEffect, useState} from "react";
import { useSelector, useDispatch } from 'react-redux'
import { singInwithPortis,} from 'redux/actions/Auth';
// import { db, auth } from 'auth/FirebaseAuth';
import { Form, Input, Alert, Row, Col, Button, Card, Modal, notification  } from "antd";
import {   GitlabOutlined, ThunderboltTwoTone, LoadingOutlined} from '@ant-design/icons';
import {metamaskInit} from 'services/MetamaskService'
import detectEthereumProvider from '@metamask/detect-provider'

const ConnectToMetamask = () => {



  const dispatch = useDispatch()
  const [isModalVisible, setIsModalVisible] = useState(false);
  const handleCancel = () => {setIsModalVisible(false);};
  let currentAccount = null;


  const showModal = () => {
	  setIsModalVisible(true)
      // metamaskInit();
      getAccount()
	  };	

  const getAccount = () => { 
			ethereum
			  .request({ method: 'eth_accounts' })
			  .then(handleAccountsChanged)
			  .catch((err) => {
				notification.warning({
							message: 'Notification',
							description: err,
							placement: 'topRight',
						  }); 
			  });  
	      }

  const handleAccountsChanged = (accounts)=> {
			 if (accounts.length === 0) {
				// MetaMask is locked or the user has not connected any accounts
				 window.ethereum.enable()
				  .catch((err) => {
					err.code == (-32002)&&
					notification.warning({
								message: 'Notification',
								description: '메타마스크 지갑에 로그인 해주세요',
								placement: 'topRight',
							  }); 
				  });  
			  } else if (accounts[0] !== currentAccount) {
				currentAccount = accounts[0];
				// Do any other work!
				console.log(currentAccount)
				  return(currentAccount)
			  }
			}

	return (
		<>
		  <div className={`d-flex align-items-center justify-content-between mb-4`}>
			<Button icon={<ThunderboltTwoTone />} type="default" block  onClick={showModal}>
				<span>MetaMask</span>
			</Button>
		   </div>
	 		<Modal title="Wallet Connect" 
			   visible={isModalVisible} 
			   onCancel={handleCancel}  
				centered={true} maskClosable={false}
		       footer={[<Button key="Cancle" onClick={handleCancel}> Cancel </Button>]}
			>
              <p><LoadingOutlined /> Connect MetaMask to TACTION to proceed</p>
        	</Modal>
  		 </>
	)
}





export default ConnectToMetamask
